mapper.select:
select distinct c.client_id from leanin_client_connect c
left join leanin_message_info m on c.client_id=m.source_client_id
where m.target_client_id=#{clientId}
and DATE_FORMAT(c.last_connected_date,'%Y-%m-%d')=#{date}

select
m.source_client_id as sourceId,target_client_id as targetId,
m.msg_type as msgType,m.msg_text as msgText,m.msg_date as msgDate
from leanin_message_info m where m. m.source_client_id=#{sourceId}
and m.target_client_id=#{targetId}

    and m.msg_date>=#{startDate}


    and m.msg_date<=#{endDate}
