mapper.update:
update leanin_book_patient set book_status=-1
where patient_id=#{patientId} and book_doc=#{doctorName}
and see_doc_time=#{bookDate}

mapper.select:
SELECT

from leanin_book_patient b
left join leanin_admin_user a
on b.registration_doc_id=a.admin_id
where b.book_status=0

  and b.patient_name like CONCAT('%',#{patientName},'%')


  and b.dept_id=#{deptId}


  and b.see_doc_time>=#{startDate}


  and b.see_doc_time<=#{endDate}

SELECT

from leanin_book_patient b
left join leanin_admin_user a
on b.registration_doc_id=a.admin_id
where b.book_status=0

  and b.see_doc_time=#{bookDate}


  and b.book_type=#{bookType}


  and b.book_doc=#{doctorName}


  and b.patient_id=#{patientId}

SELECT patient_name as patientName, CASE patient_sex WHEN 1 THEN '男' WHEN 2 THEN '女' ELSE '其他' END AS sex, id_card as idCard, phone_num as phoneNum, book_doc as bookDoc, registration_dept as bookDept, see_doc_time as seeDocTime, book_time as bookTime, CASE book_source WHEN 1 THEN 'pc' WHEN 2 THEN '公众号' ELSE '其他' END AS bookSource FROM `leanin_book_patient` WHERE book_status = 0
and patient_name like CONCAT('%',#{patientName},'%')
and dept_id =#{deptId}
and see_doc_time>=#{startDate}
and see_doc_time<=#{endDate}

SELECT lbp.patient_name as patientName, CASE lbp.patient_sex WHEN 1 THEN '男' WHEN 2 THEN '女' ELSE '其他' END AS sex, lbp.id_card as idCard, lbp.phone_num as phoneNum, lbp.book_doc as bookDoc, lbp.registration_dept as dept, lbp.book_time as bookTime, lau.admin_name as registrationDoc, CASE lbp.advice_type WHEN '0' THEN '未就诊' WHEN '1' THEN '已就诊' ELSE '其他' END AS status FROM `leanin_book_patient` lbp,`leanin_admin_user` lau WHERE lbp.book_status = 0 and lbp.registration_doc_id = lau.admin_id
and lbp.patient_name like CONCAT('%',#{patientName},'%')
and lbp.dept_id =#{deptId}
and lbp.see_doc_time>=#{startDate}
and lbp.see_doc_time<=#{endDate}
