mapper.select:
select patient_info_id as patientInfoId, patient_info_name as patientInfoName, patient_info_sex as patientInfoSex, TIMESTAMPDIFF(YEAR, @patient_info_birthday, CURDATE()) as patientAge, patient_info_idcard as patientInfoIdcard, patient_info_phone as patientInfoPhone, patient_info_marr_status as patientInfoMarrStatus, patient_info_national as patientInfoNational, patient_info_culture as patientInfoCulture,patient_info_job as patientInfoJob, patient_info_job_addre as patientInfoJobAddre, patient_info_addre as patientInfoAddre, hospital_area_code as hospitalAreaCode,emer_cont_name as emerContName, emer_cont_relation as emerContRelation, emer_cont_phone as emerContPhone, admin_creater as adminCreater,create_time as createTime from leanin_patient_info where 1=1
and patient_info_sex=#{sex,jdbcType=INTEGER}
and TIMESTAMPDIFF(YEAR, @patient_info_birthday, CURDATE())>=#{startAge,jdbcType=INTEGER}
and TIMESTAMPDIFF(YEAR, @patient_info_birthday, CURDATE())<=#{endAge,jdbcType=INTEGER}
and patient_info_phone is not null
and patient_info_phone is null
order by create_time desc

select

from leanin_patient_info where 1=1

    and hospital_area_code=#{areaCode,jdbcType=VARCHAR}


    and patient_info_name like CONCAT('%',#{patientName},'%')


    and patient_info_id like CONCAT('%',#{patientId},'%')


    and create_time>=#{beginDate}


    and create_time<=#{endDate}

order by create_time desc

select

from leanin_patient_info where patient_info_id = #{patientInfoId,jdbcType=VARCHAR}

    and hospital_area_code=#{areaCode,jdbcType=VARCHAR}

select

from leanin_patient_info
where patient_info_id = #{patientInfoId}


        and (patient_source = 1 or patient_source = 3)


        and patient_source = #{patientSource}
