SELECT

FROM leanin_plan_patient
WHERE patient_status = 1
AND plan_num IN (
    SELECT pi.plan_num
    FROM leanin_plan_info pi
    WHERE pi.plan_status >= 0
    AND pi.plan_type = #{myFollowReq.planType}

        AND pi.plan_num = #{myFollowReq.planId}



            AND pi.plan_duty_per = #{myFollowReq.userId}


            AND pi.plan_creater = #{myFollowReq.userId}





)

    AND next_date >= #{myFollowReq.startDate}


    AND next_date <= #{myFollowReq.startDate}






        and plan_pats_status between 3 and 11


        and plan_pats_status=#{myFollowReq.folStatus}






    AND patient_name like CONCAT('%',#{myFollowReq.patName},'%')


    AND send_type = 2

select

from leanin_plan_patient
where patient_status = 1
and patient_name = #{patientName}
and patient_phone = #{phoneNum}
and id_card = #{idCard}

select
from leanin_plan_patient where patient_status = 1 and plan_num in
#{item}

SELECT

FROM leanin_plan_patient
WHERE patient_id=#{patientId}
AND plan_num = #{planNum}
AND patient_status = 1

select count(*) c,lpp.plan_pats_status
from leanin_plan_patient lpp,leanin_plan_info lpi
where lpp.patient_status = 1
and lpp.plan_num = lpi.plan_num
and lpi.plan_status =0

    and lpi.plan_type =#{planType}



    and lpi.plan_duty_per = #{userId}


    and lpp.form_status =#{formStatus}


    and lpp.plan_num = #{planNum}


    and lpp.patient_source = #{patientSource}


    and lpp.patient_ward like CONCAT('%',#{dept},'%')


    and lpp.next_date >= #{startDate}


    and lpp.next_date <= #{endDate}

group by lpp.plan_pats_status
order by lpp.plan_pats_status

SELECT

FROM leanin_plan_patient
WHERE patient_status = 1
and patient_id=#{patientId}
and plan_num = #{planNum}

SELECT COUNT(*) as hitCount, lpp.plan_pats_status as state , lwi.`ward_name` as deptName FROM leanin_plan_patient lpp, leanin_plan_info lpi, `leanin_ward_info` lwi WHERE lpp.patient_status = 1 AND lpp.plan_num = lpi.plan_num AND lpi.plan_type = 1 AND lpi.plan_status = 0 AND lpp.`patient_ward` = lwi.`ward_name`
and lpp.plan_num = #{planNum}
and lpp.patient_source = #{patientSource}
and lpp.patient_ward = #{dept}
and lpp.next_date >= #{startDate}
and lpp.next_date <= #{endDate}
GROUP BY lwi.`ward_name`, lpp.plan_pats_status ORDER BY lpp.plan_pats_status

SELECT COUNT(distinct(lpp.patient_id) ) as hitCount, -4 as state , lwi.`ward_name` as deptName FROM leanin_plan_patient lpp, leanin_plan_info lpi, `leanin_ward_info` lwi WHERE lpp.patient_status = 1 AND lpp.plan_num = lpi.plan_num AND lpi.plan_type = 1 AND lpi.plan_status = 0 AND lpp.`patient_ward` = lwi.`ward_name` AND lpp.plan_pats_status = 2
and lpp.plan_num = #{planNum}
and lpp.patient_source = #{patientSource}
and lpp.patient_ward = #{dept}
and lpp.next_date >= #{startDate}
and lpp.next_date <= #{endDate}
GROUP BY lwi.`ward_name` ORDER BY lpp.plan_pats_status

SELECT COUNT(DISTINCT (lpp.patient_id)) AS hitCount, MONTH(lpp.`next_date`) as followUpMonth, -7 as state FROM `leanin_plan_patient` lpp, `leanin_plan_info` lpi WHERE lpp.`plan_num` = lpi.`plan_num` AND lpi.`plan_type` =1 AND lpi.`plan_status` = 0 AND lpp.`patient_status` = 1 AND lpp.`plan_pats_status` =2 AND YEAR(lpp.`next_date`) = #{year}
and lpp.patient_ward = #{dept}
and lpp.plan_num = #{planNum}
and lpp.patient_source = #{patientSource}
GROUP BY MONTH(lpp.`next_date`)

select

from leanin_plan_patient
where patient_status = 1
and send_type != #{sendStatus}
and plan_num = #{planNum}
limit 1
